url = 'http://www.hytung.com.cn/'

def get_chengyu():
    chengyu_list = []
    with open('chengyu.txt') as f:
        results = f.readlines()
        for res in results:
            try:
                word = res.split(':')[0].strip()
                account_obj = {
                    'word': word,
                }
                chengyu_list.append(account_obj)
            except:
                print('该行文本格式有误')
                print(res)
                with open('failed.txt', 'a') as ff:
                    ff.write(res)

    return chengyu_list

def get_res(word):
    if type == 'all':
        chengyu_list = get_chengyu()
    else:
        chengyu_list = [{'word': type}]

    for chengyu in chengyu_list:
        try:
            print('当前查询成语:' + chengyu['word'])
            keyword =quote(chengyu['word'])
            for i in range(1,11):
                print('当前页数：'+str(i))
                pageToken = str(i)
                data = body.format(keyword=keyword,pageToken=pageToken)

                response = requests.post(url,headers=headers,data=data,timeout=15)
                html = HTML(response.text)
                all_tr = html.xpath('//table[@id="DataList"]//tr')
                for item in all_tr:
                    detail_html_text = etree.tostring(item)
                    detail_html = HTML(detail_html_text.decode())
                    chuchu_list = detail_html.xpath('//div[@class="derivation"]//text()')
                    content_list = detail_html.xpath('//div[@class="summary"]//text()')
                    chuchu = ''.join(chuchu_list).replace('\n',' ').replace('\r',' ').replace('\t',' ').replace(',','，').strip()
                    content = ''.join(content_list).replace('\n',' ').replace('\r',' ').replace('\t',' ').replace(',','，').strip()
                    save_res = chengyu['word']+','+ chuchu +','+content+'\n'
                    print(save_res)
                    with open('results.csv', 'a',encoding='gbk',errors='ignore') as f:
                        f.write(save_res)
        except:
            print('未知错误')
            with open('failed.csv', 'a') as f:
                f.write(chengyu['word']+'\n')

if __name__ == '__main__':
    while True:
        type = input('请输入要查询的成语(如需批量请输入 all):')
        if type == 'all':
            get_res('all')
        else:
            get_res(type)
